import type {
  AccountInfo,
  AdapterPlugin,
  NetworkInfo,
  SignMessagePayload,
  SignMessageResponse,
  WalletName,
} from "@aptos-labs/wallet-adapter-core";
import {
  AptosWalletErrorResult,
  NetworkName,
  PluginProvider,
} from "@aptos-labs/wallet-adapter-core";
import { TxnBuilderTypes, Types } from "aptos";

interface PontemWindow extends Window {
  pontem?: PluginProvider;
}

const PontemNetworkNameMapping = {
  "Aptos mainnet": NetworkName.Mainnet,
  "Aptos testnet": NetworkName.Testnet,
  "Aptos devnet": NetworkName.Devnet,
};

type PontemNetworkNames = keyof typeof PontemNetworkNameMapping;

interface PontemPluginProvider
  extends Omit<PluginProvider, "network" | "onNetworkChange"> {
  network: () => Promise<
    { name: PontemNetworkNames; chainId?: string; api?: string } | NetworkName
  >;
  onNetworkChange: (
    listener: (newNetwork: {
      networkName?: NetworkInfo;
      name?: PontemNetworkNames;
      chainId?: string;
      api?: string;
    }) => Promise<void>,
  ) => Promise<void>;
  publicKey?: () => Promise<string>;
  signAndSubmit?: (
    transaction: any,
    options?: any,
  ) => Promise<{ success: boolean; result: { hash: string } }>;
}

declare const window: PontemWindow;

export const PontemWalletName = "Pontem" as WalletName<"Pontem">;

export class PontemWallet implements AdapterPlugin {
  readonly name = PontemWalletName;
  readonly url = "https://onelink.to/t3sae5";
  readonly icon =
    "data:image/svg+xml;base64,";

  provider: PontemPluginProvider | undefined =
    typeof window !== "undefined" ? window.pontem : undefined;

  deeplinkProvider(data: { url: string }): string {
    return `pontem-wallet://link?url=${data.url}`;
  }

  async connect(): Promise<AccountInfo> {
    try {
      const accountInfo = await this.provider?.connect();
      if (!accountInfo) throw `${PontemWalletName} Address Info Error`;
      return accountInfo;
    } catch (error: any) {
      throw error;
    }
  }

  async account(): Promise<AccountInfo> {
    const response = await this.provider?.account();
    if (!response) throw `${PontemWalletName} Account Error`;
    let publicKey = "";
    if (this.provider?.publicKey) {
      publicKey = await this.provider?.publicKey();
    }
    return { ...response, publicKey };
  }

  async disconnect(): Promise<void> {
    try {
      await this.provider?.disconnect();
    } catch (error: any) {
      throw error;
    }
  }

  async signAndSubmitTransaction(
    transaction: any,
    options?: any
  ): Promise<{ hash: Types.HexEncodedBytes }> {
    try {
      if (!this.provider?.signAndSubmit) {
        throw `${PontemWalletName} Sign and Submit failed`;
      }
      const response = await this.provider?.signAndSubmit(
        transaction,
        { ...options, max_gas_amount: options?.max_gas_amount?.toString() }
      );

      if (!response || !response.success) {
        throw new Error('No response');
      }

      if ((response as unknown as AptosWalletErrorResult)?.code) {
        throw new Error((response as unknown as AptosWalletErrorResult)?.message);
      }
      const hash = response.result.hash;

      return { hash } as { hash: Types.HexEncodedBytes };
    } catch (error: any) {
      const errMsg = error.message;
      throw errMsg;
    }
  }

  async signMessage(message: SignMessagePayload): Promise<SignMessageResponse> {
    try {
      if (typeof message !== "object" || !message.nonce) {
        `${PontemWalletName} Invalid signMessage Payload`;
      }
      const response = await this.provider?.signMessage(message);
      if (response) {
        return response;
      } else {
        throw `${PontemWalletName} Sign Message failed`;
      }
    } catch (error: any) {
      const errMsg = error.message;
      throw errMsg;
    }
  }

  async network(): Promise<
    { name: NetworkName; chainId?: string; api?: string } | NetworkName
  > {
    try {
      const response = await this.provider?.network();
      if (!response) throw `${PontemWalletName} Network Error`;
      if (typeof response === "object" && response?.name) {
        return {
          name: PontemNetworkNameMapping[
            response.name as keyof typeof PontemNetworkNameMapping
          ],
          chainId: response?.chainId ?? undefined,
          api: response?.api ?? undefined,
        };
      }
      return response as NetworkName;
    } catch (error: any) {
      throw error;
    }
  }

  async onNetworkChange(callback: any): Promise<void> {
    try {
      const handleNetworkChange = async (newNetwork: {
        networkName?: NetworkInfo;
        name?: PontemNetworkNames;
        chainId?: string;
        api?: string;
      }): Promise<void> => {
        if (newNetwork?.name) {
          callback({
            name: PontemNetworkNameMapping[newNetwork.name],
            chainId: newNetwork?.chainId ?? undefined,
            api: newNetwork?.api ?? undefined,
          });
        }
        if (newNetwork?.networkName) {
          callback({
            name: newNetwork.networkName,
            chainId: undefined,
            api: undefined,
          });
        }
      };
      await this.provider?.onNetworkChange(handleNetworkChange);
    } catch (error: any) {
      const errMsg = error.message;
      throw errMsg;
    }
  }

  async onAccountChange(callback: any): Promise<void> {
    try {
      const handleAccountChange = async (
        newAccount: AccountInfo,
      ): Promise<void> => {
        if (newAccount?.publicKey) {
          callback({
            publicKey: newAccount.publicKey,
            address: newAccount.address,
          });
        } else {
          const response = await this.connect();
          callback({
            address: response?.address,
            publicKey: response?.publicKey,
          });
        }
      };
      await this.provider?.onAccountChange(handleAccountChange);
    } catch (error: any) {
      const errMsg = error.message;
      throw errMsg;
    }
  }
}
